mapper.update:
UPDATE attendance
SET
    status = #{status},
    attendance_time = NOW(),
    remark = #{remark}
WHERE user_id = #{userId}

UPDATE attendance
SET
status = CASE

    WHEN id = #{item.id} THEN #{item.status}

END,
attendance_time = CASE

    WHEN id = #{item.id} THEN #{item.attendanceTime}

END,
remark = CASE

    WHEN id = #{item.id} THEN #{item.remark}

END
WHERE id IN

    #{item.id}

mapper.delete:
DELETE a
FROM attendance a
JOIN user u ON a.user_id = u.id


        AND u.name = #{name}


        AND u.class_name = #{className}


        AND u.grade = #{grade}
